import React from "react";
import { Button, Card, Form } from "react-bootstrap";
import { useState } from "react";

function User({ user, index, deleteUser }) {
  return (
    <div className="user">
      <table>
        <tbody>
          <tr>
            <td className="element">{user.id}</td>
            <td className="element">{user.firstName}</td>
            <td className="element">{user.lastName}</td>
            <td className="element">{user.role}</td>
            <td className="element">{user.email}</td>
            <td className="element">
              <Button
                variant="outline-danger"
                onClick={() => deleteUser(index)}
              >
                ✕
              </Button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

function UserForm({ addUser }) {
  const [id, setId] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [role, setRole] = useState("");
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    let user = { id, firstName, lastName, role, email };
    e.preventDefault();
    if (!id || !firstName || !lastName || !email || !role) return;

    addUser(user);

    setId("");
    setFirstName("");
    setLastName("");
    setRole("");
    setEmail("");
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group>
        <Form.Label>
          <b>ID</b>
        </Form.Label>
        <Form.Control
          type="text"
          className="input"
          value={id}
          onChange={(e) => setId(e.target.value)}
        />
        <Form.Label>
          <b>First name</b>
        </Form.Label>
        <Form.Control
          type="text"
          className="input"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <Form.Label>
          <b>Last name</b>
        </Form.Label>
        <Form.Control
          type="text"
          className="input"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />
        <Form.Label>
          <b>Role</b>
        </Form.Label>
        <Form.Control
          type="text"
          className="input"
          value={role}
          onChange={(e) => setMob(e.target.value)}
        />
        <Form.Label>
          <b>Email</b>
        </Form.Label>
        <Form.Control
          type="email"
          className="input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </Form.Group>
      <Button variant="primary mb-3" type="submit">
        Submit
      </Button>
    </Form>
  );
}

function App() {
  const [users, setUsers] = useState([
    {
      id: "ID,",
      firstName: "First Name",
      lastName: "Last Name",
      email: "Email",
      mob: "Mobile Number",
    },
  ]);

  const addUser = (user) => {
    const newUsers = [...users, user];
    setUsers(newUsers);
  };

  const deleteUser = (index) => {
    const newUsers = [...users];
    newUsers.splice(index, 1);
    setUsers(newUsers);
  };

  return (
    <div className="app">
      <div className="container">
        <h1 className="text-center mb-4">User list</h1>
        <UserForm addUser={addUser} />
        <div>
          {users.map((user, index) => (
            <Card>
              <Card.Body>
                <User user={user} index={index} deleteUser={deleteUser} />
              </Card.Body>
            </Card>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
