import React, { PureComponent } from 'react';
import { Button, Form, Input } from 'antd';

class EditButton extends PureComponent {


    constructor(props) {
        super(props);
        this.state = {
            updateusershow: '',
            Name: '',
            phone: '',
            email: '',
            bloodgroup: ''
        }
    };

    entername = (e) => {
        let val = e.target.value;
        this.setState({ Name: val });
        console.log(this.state.phone);

    }


    enterphoneno = (e) => {
        let val = e.target.value;
        this.setState({ phone: val });
    }

    enteremail = (e) => {
        let val = e.target.value;
        this.setState({ email: val });
    }

    enterebloodgroup = (e) => {
        let val = e.target.value;
        this.setState({ bloodgroup: val });
    }
render() {


        let updateuserdetails =
            <div>
                <Form>
                    <label>
                        Name :
                        <Input defaultValue={this.props.user.userName} placeholder='Enter name' name="name"

                            onChange={this.entername} />
                    </label>
                    <label>
                        Phone no. :
                        <Input style={{ width: '100%' }} placeholder="+91 7484977543"
                            defaultValue={this.props.user.userPhoneNo} name="name"
                            onChange={this.enterphoneno} />
                    </label>
                    <label>
                        Email :
                        <Input name="name" defaultValue={this.props.user.userEmail} placeholder="Enter email"
                            onChange={this.enteremail} />
                    </label>
                    <label>
                        Blood Group :
                        <Input name="name" defaultValue={this.props.user.bloodGroup} placeholder="Enter Blood Group"
                            onChange={this.enterebloodgroup} />
                    </label>
<Button
                        onClick={() => {


                            let userdetails = {
                                userName: this.state.Name,
                                userPhoneNo: this.state.phone,
                                userEmail: this.state.email,
                                bloodGroup: this.state.bloodgroup
                            }

                           //console.log(userdetails.userName);

                            if (userdetails.userName === '' || userdetails.userPhoneNo === '' ||
                                userdetails.userEmail === ''|| userdetails.bloodGroup === '') {
                                alert("Please fill all the required detail");

                            }
                                else {

                                    this.props.edituser(userdetails, this.props.id);

                                    this.setState({
                                        Name: userdetails.userName,
                                        phone: userdetails.userPhoneNo,
                                        email: userdetails.userEmail,
                                        bloodgroup: userdetails.bloodGroup
                                    })

                                    this.setState({ updateusershow: '' });
                                }
                            }
                        }>Update</Button>

                    <Button onClick={() => {
                        this.setState({
                            Name: this.props.user.userName,
                            phone: this.props.user.userPhoneNo,
                            email: this.props.user.userEmail,
                            bloodgroup: this.props.user.bloodGroup
                        })

                        this.setState({ updateusershow: '' });
                    }}>Cancel</Button>


                </Form>
            </div>

        return (
            <div>
                <Button onClick={() => {
                    this.setState({ 
                        Name: this.props.user.userName,
            phone: this.props.user.userPhoneNo,
            email: this.props.user.userEmail,
            bloodgroup: this.props.user.bloodGroup
                    })
                    this.setState({ updateusershow: updateuserdetails })
                }}>Edit</Button>

                <div>{this.state.updateusershow}</div>
            </div>
        )
    }
}

export default EditButton;
