import React, { PureComponent } from "react";
import { Form, Button, Input } from "antd";

class AddButton extends PureComponent {
  constructor() {
    super();
    this.state = {
      Name: "",
      phone: "",
      email: "",
      bloodgroup: "",
    };
  }

  entername = (e) => {
    let val = e.target.value;
    this.setState({ Name: val });
  };

  enterphoneno = (e) => {
    let val = e.target.value;
    this.setState({ phone: val });
  };

  enteremail = (e) => {
    let val = e.target.value;
    this.setState({ email: val });
  };

  enterbloodgroup = (e) => {
    let val = e.target.value;
    this.setState({ bloodgroup: val });
  };
  render() {
    return (
      <div>
        <Form>
          <label>
            Name :
            <Input
              value={this.state.Name}
              placeholder="Enter name"
              name="name"
              onChange={this.entername}
            />
          </label>
          <label>
            Phone no. :
            <Input
              style={{ width: "100%" }}
              placeholder="+91 7484977543"
              value={this.state.phone}
              name="name"
              type="number"
              onChange={this.enterphoneno}
            />
          </label>
          <label>
            Email :
            <Input
              name="name"
              value={this.state.email}
              placeholder="Enter Email"
              type="email"
              onChange={this.enteremail}
            />
          </label>
          <label>
            Blood Group :
            <Input
              name="name"
              value={this.state.bloodgroup}
              placeholder="Enter Blood Group"
              onChange={this.enterbloodgroup}
            />
          </label>
          <Button
            onClick={() => {
              let userdetails = {
                userName: this.state.Name,
                userPhoneNo: this.state.phone,
                userEmail: this.state.email,
                bloodGroup: this.state.bloodgroup,
              };
              if (
                userdetails.userName === "" ||
                userdetails.userPhoneNo === "" ||
                userdetails.userEmail === "" ||
                userdetails.bloodGroup === ""
              ) {
                alert("Please fill all the required detail");
              } else {
                this.props.Adduser(userdetails);
                this.setState({
                  Name: "",
                  phone: "",
                  email: "",
                  bloodgroup: "",
                });
              }
            }}
          >
            Add
          </Button>
        </Form>
      </div>
    );
  }
}

export default AddButton;
