function UserForm({ addUser }) {
  const [id, setId] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [role, setRole] = useState("");
  const [email, setEmail] = useState("");

  const handleFinish = (e) => {
    let user = { id, firstName, lastName, role, email };
    e.preventDefault();
    if (!id || !firstName || !lastName || !role || !email) return;
    addUser(user);
    setId("");
    setFirstName("");
    setLastName("");
    setRole("");
    setEmail("");
  };

  return (
    <Form onFinish={handleFinish}>
      <Form.List>
        <Form.Item label="ID" className="input" value={id}></Form.Item>
        <Form.Item
          label="First Name"
          className="input"
          value={firstName}
        ></Form.Item>
        <Form.Item
          label="Last Name"
          className="input"
          value={lastName}
        ></Form.Item>
        <Form.Item label="Role" className="input" value={role}></Form.Item>
        <Form.Item label="Email" className="input" value={email}></Form.Item>
      </Form.List>
      <Button>Submit</Button>
    </Form>
  );
}

function AddUser() {
  const [users, setUsers] = useState([
    {
      id: "ID",
      firstName: "First Name",
      lastName: "Last Name",
      role: "Role",
      email: "Email",
    },
  ]);

  const addUser = (user) => {
    const newUsers = [...users, user];
    setUsers(newUsers);
  };

  return (
    <div>
      <header className="header">
        <h4>User List</h4>
      </header>
      <UserForm addUser={addUser} />
    </div>
  );
}

export default AddUser;

