import React, { PureComponent } from "react";
import { Button, Form, Input } from "antd";

class EditUser extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      updated: "",
      id:"",
      firstName: "",
      lastName: "",
      role: "",
      email: "",
    };
  }
  enterId = (e) => {
    let val = e.target.value;
    this.setState({ id: val });
  };

  enterFirstName = (e) => {
    let val = e.target.value;
    this.setState({ firstName: val });
  };

  enterLastName = (e) => {
    let val = e.target.value;
    this.setState({ lastName: val });
  };

  enterRole = (e) => {
    let val = e.target.value;
    this.setState({ role: val });
  };

  enterEmail = (e) => {
    let val = e.target.value;
    this.setState({ email: val });
  };
  render() {
    let updateuserdetails = (
      <div>
        <Form>
          <label>
            ID :
            <Input
              defaultValue={this.props.user.userId}
              placeholder="Enter ID"
              name="name"
              onChange={this.enterId}
            />
          </label>
          <label>
            First Name :
            <Input
              placeholder="Enter First Name"
              defaultValue={this.props.user.userFirstName}
              name="name"
              onChange={this.enterFirstName}
            />
          </label>
          <label>
            Last Name :
            <Input
              placeholder="Enter Last Name"
              defaultValue={this.props.user.userLastName}
              name="name"
              onChange={this.enterLastName}
            />
          </label>
          <label>
            Role :
            <Input
              name="name"
              defaultValue={this.props.user.userRole}
              placeholder="Enter Role"
              onChange={this.enterRole}
            />
          </label>
          <label>
            Email :
            <Input
              name="name"
              defaultValue={this.props.user.userEmail}
              placeholder="Enter email"
              onChange={this.enterEmail}
            />
          </label>
          <Button
            onClick={() => {
                let userdetails = {
                  userId: this.state.id,
                  userFirstName: this.state.firstName,
                  userLastName: this.state.lastName,
                  userRole: this.state.role,
                  userEmail: this.state.email,
                };
              //console.log(userdetails.userName);

              if (
                userdetails.userId === "" ||
                userdetails.userFirstName === "" ||
                userdetails.userLastName === "" ||
                userdetails.userRole === "" ||
                userdetails.userEmail ===""
              ) {
                alert("Please fill all the required detail");
              } else {
                this.props.edituser(userdetails, this.props.id);

                this.setState({
                    id: userdetails.userId,
                  firstName: userdetails.userFirstName,
                  lastName: userdetails.userLastName,
                  role: userdetails.userRole,
                  email: userdetails.userEmail,
                });

                this.setState({ updated: "" });
              }
            }}
          >
            Update
          </Button>

          <Button
            onClick={() => {
              this.setState({
                id: this.props.user.userId,
              firstName: this.props.user.userFirstName,
              lastName: this.props.user.userLastName,
              role: this.props.user.userRole,
              email: this.props.user.userEmail,
              });

              this.setState({ updated: "" });
            }}
          >
            Cancel
          </Button>
        </Form>
      </div>
    );

    return (
      <div>
        <Button
          onClick={() => {
            this.setState({
                id: this.props.user.userId,
              firstName: this.props.user.userFirstName,
              lastName: this.props.user.userLastName,
              role: this.props.user.userRole,
              email: this.props.user.userEmail,
            });
            this.setState({ updated: updateuserdetails });
          }}
        >
          Edit
        </Button>

        <div>{this.state.updated}</div>
      </div>
    );
  }
}

export default EditUser;
